from pathlib import Path;import json,sys;p=Path(sys.argv[1] if len(sys.argv)>1 else 'artifacts/staging/draft_youtube.txt');t=p.read_text() if p.exists() else 'No content';m={'title':p.stem.replace('_',' ').title(),'description':t[:200],'tags':['monkey paw','auto'],'thumbnail':str(p.with_suffix('.png').name)};o=p.with_suffix('.meta.json');o.write_text(json.dumps(m,indent=2));print(o)